import React, { useContext } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
    const { budget } = useContext(AppContext);
    let adjust = budget;
    let inputClass = 'budget';
    let button = 'btn btn-primary';
    const handleEdit = () => {
        adjust = true;
        inputClass = '';
    }
    

    return (
        <div className='alert alert-secondary'>
            {<span>Budget: ${budget && adjust}<input type='text' id='budget' value={budget} className={inputClass}></input></span>}
            <span style={{ float: 'right'}}><button className={button} onClick={handleEdit}>Edit</button></span>
        </div>
    );
};

export default Budget;
